mapper.select:
select rentid as rentid, price as price, begindate as begindate, returndate as returndate, rentflag as rentflag, identity as identity, carnumber as carnumber, opername as opername, createtime as createtime from bus_rent
and rentid like concat("%",#{req.rentid},"%")
and carnumber like concat("%",#{req.carnumber},"%")
and identity like concat("%",#{req.identity},"%")
and createtime >=#{req.startTime}
and createtime <=#{req.endTime}
and rentflag=#{req.rentflag}
order by createtime desc

mapper.insert:
insert into bus_rent (rentid, price, begindate, 
  returndate, rentflag, identity, 
  carnumber, opername, createtime
  )
values (#{rentid,jdbcType=VARCHAR}, #{price,jdbcType=DOUBLE}, #{begindate,jdbcType=TIMESTAMP}, 
  #{returndate,jdbcType=TIMESTAMP}, #{rentflag,jdbcType=INTEGER}, #{identity,jdbcType=VARCHAR}, 
  #{carnumber,jdbcType=VARCHAR}, #{opername,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
  )
