mapper.select:
select id as id, loginname as loginname, loginip as loginip, logintime as logintime from sys_log_login
and loginname like concat("%",#{loginname},"%")
and loginip like concat("%",#{loginip},"%")
and logintime >= #{startTime}
and logintime < = #{endTime} and logintime <= #{endTime}
order by logintime desc

mapper.update:
update sys_log_login
set loginname = #{loginname,jdbcType=VARCHAR},
loginip = #{loginip,jdbcType=VARCHAR},
logintime = #{logintime,jdbcType=TIMESTAMP}
where id = #{id,jdbcType=INTEGER}
