mapper.select:
SELECT t1.userid AS userid, t1.loginname AS loginname, t1.identity AS identity, t1.realname AS realname, t1.sex AS sex, t1.address AS address, t1.phone AS phone, t1.position AS position, t1.type AS type, t1.available AS available, GROUP_CONCAT(DISTINCT t3.rolename) AS rolename, max(t4.logintime) as currentTime from sys_user as t1 left join sys_role_user as t2 on t1.userid = t2.uid left join sys_role as t3 on t3.roleid = t2.rid left join sys_log_login as t4 on t4.loginname = CONCAT(t1.realname,'-',t1.loginname)
and t1.realname like concat("%",#{req.realname},"%")
and t1.loginname like concat("%",#{req.loginname},"%")
and t1.identity = #{req.identity}
and t1.address like concat("%",#{req.address},"%")
and t1.phone = #{req.phone}
and t1.available = #{req.available}
and t1.sex = #{req.sex}
and t1.type != 1
GROUP BY CONCAT(t1.realname,'-',t1.loginname)

mapper.update:
UPDATE sys_user
SET pwd = #{user.pwd}
WHERE userid = #{user.userid}

UPDATE sys_user
SET pwd = #{user.pwd}
WHERE userid = #{user.userid}
